CREATE TABLE `invoice` (
  `idInvoice` char(15) NOT NULL,
  `invcusid` char(15) DEFAULT NULL COMMENT 'Customer/Prospect id',
  `invaddid` char(15) DEFAULT NULL COMMENT 'Delivery address id',
  `invDlvAddId` char(15) DEFAULT NULL,
  `invcrtdate` datetime DEFAULT NULL COMMENT 'Quote creation date',
  `invcrtuserid` char(15) DEFAULT NULL COMMENT 'Creation user',
  `invupddate` datetime DEFAULT NULL COMMENT 'Last update date.',
  `invupduserid` char(15) DEFAULT NULL COMMENT 'Last update user id',
  `invDate` date NOT NULL,
  `invduedate` date NOT NULL COMMENT 'Due date',
  `invStatus` char(5) NOT NULL COMMENT 'Quote status. Values are predefined in Code Tables. f.e. W(Work in progress), C(confirmed), O(ordered), D(declined)',
  `invType` char(5) NOT NULL COMMENT 'Quote Type. Values are predefined in Code Tables. f.e. O (price offer), P(Proposal)',
  `invVat` tinyint(1) NOT NULL COMMENT 'Liable for Vat',
  `invHeaderComments` varchar(256) DEFAULT NULL,
  `Active` tinyint(1) NOT NULL COMMENT 'Active (T/F)',
  PRIMARY KEY (`idInvoice`),
  KEY `FK_QTECustomer` (`invcusid`),
  KEY `FK_QTEAddress` (`invaddid`),
  KEY `FK_InvStatus` (`invStatus`),
  KEY `FK_InvType` (`invType`),
  KEY `FK_InvDlvAddId` (`invDlvAddId`),
  CONSTRAINT `FK_InvAddress` FOREIGN KEY (`invaddid`) REFERENCES `address` (`idAddress`),
  CONSTRAINT `FK_InvCustomer` FOREIGN KEY (`invcusid`) REFERENCES `customer` (`idCus`),
  CONSTRAINT `FK_InvDlvAddId` FOREIGN KEY (`invDlvAddId`) REFERENCES `address` (`idAddress`)
