/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `getLastNumber`(in category char(5), in nyear dec(4.0), out success boolean)
BEGIN

    declare lastNbr char(15) ;

    declare lnumber decimal(15.0);

    

  select nbrLstValue into lastNbr from number where nbrCategory = category and  nbrYear = nyear;  

  

  set lnumber = cast(lastNbr as decimal(15.0));

  set lnumber = lnumber + 1;

  set lastNbr = cast(lnumber as char);



    update number set nbrLstValue = lastNbr

    where nbrCategory = category and  nbrYear = nyear;     

    set success = @rows; 

    if (@rows = 1) then

     commit;

     else

     rollback;

    end if;    

END */;;
